def quardtree(image):
    n = len(image)
    if n == 1:
        return image[0]
    check = image[0][0]
    all_same = True
    for i in range(n):
        for j in image[i]:
            if j != check:
                all_same = False
                break
        if not all_same:
            break
    if all_same:
        return check
    box1 = []
    box2 = []
    box3 = []
    box4 = []
    n //= 2
    for i in range(n):
        box1.append(image[i][:n])
        box2.append(image[i][n:])
        box3.append(image[n + i][:n])
        box4.append(image[n + i][n:])
    return '(' + quardtree(box1) + quardtree(box2) + quardtree(box3) + quardtree(box4) + ')'


n = int(input())
print(quardtree([input() for _ in range(n)]))
